import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Component } from 'react';
import { Button } from 'reactstrap';

function Botonera(props) {
  if (props.jugar === true) {
    let fichas = Array.fill(8)
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: [],
      playable: false
    };
  }

  jugar() {
    let jugTmp = this.state.playable
    let listBotTmp = this.state.listaBotones

    jugTmp = true
    for (let i = 1; i <= 8; i++) {
      for (let j = 1; j <= 4; j++) {
        if (i >= 6) {
          listBotTmp.push('secondary')
        } else {
          listBotTmp.push('success')
        }
      }
    }

    this.setState({
      listaBotones: listBotTmp,
      playable: jugTmp
    });
  }

  render() {
    return (
      <div className="App">
        <Button onClick={() => this.jugar()}>Jugar</Button>
        <Botonera jugar={this.state.playable} matriz={this.state.listaBotones} />
      </div>
    );
  }
}

export default App;
