import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Component } from 'react';
import { Button } from 'reactstrap';

function Botonera(props) {
  let tablero = [];
  if (props.jugar) {
    for (let i = 1; i <= 8; i++) {
      for (let j = 1; j <= 4; j++) {
        if (i >= 6) {

            tablero[i][j] = <Button color={props.matriz[0]} />


        } else {
          if (j % 2 === 0) {
            tablero[i][j] = <Button color={props.matriz[1]} />
          } else {
            tablero[i][j] = <Button color={props.matriz[2]} />
            tablero[i][j+1] = <Button color={props.matriz[1]} />
          }

        }
      }
    }
  }
  return (
    tablero.map(e=>e)
  );

}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listaBotones: ["secondary","success", "outline"],
      playable: false
    };
  }

  jugar() {
    let jugTmp = this.state.playable

    if(jugTmp) {
      jugTmp = false
    } else {
      jugTmp = true
    }

    this.setState({
      playable: jugTmp
    });
  }

  render() {
    return (
      <div className="App">
        <Button onClick={() => this.jugar()}>Jugar</Button>
        <Botonera jugar={this.state.playable}  matriz={this.state.listaBotones} />
      </div>
    );
  }
}

export default App;
